export function Coupures(params) {

    let default_params = {
        amount: 0,
        devise: "€"
    };

    params = Object.assign(default_params, params);

    let bills = [];
    if(params.devise === "€") {
        bills = [500,200,100,50, 20, 10, 5, 2, 1, 0.5, 0.2, 0.1, 0.05, 0.02, 0.01];
    } else if (params.devise === "$") {
        bills = [100,50,20,10,5,2,1,0.5,0.25,0.10,0.05,0.01];
    } else if (params.devise === "£") {
        bills = [50,20,10,5,2,1,0.5,0.2,0.1,0.05,0.02,0.01];
    }
    else {
        return "Devise inconnue";
    }
    let result = `\n${params.amount} ${params.devise}\n`;
    bills.forEach(bill => {
        if(params.amount >= bill){
            let bill_count = Math.floor(params.amount / bill);
            params.amount -= bill * bill_count;
            result += `\n${bill_count} x ${bill} ${params.devise}`;
        }
    });
    return result;
}